mapper.update:
update board set
hits = hits + 1
where board_num = #{boardnum}

mapper.select:
select replynum, user_id, commentt, to_char(inputdate, 'YYYY-MM-DD') as inputdate
from reply where board_num = #{boardnum}
order by replynum

mapper.update:
update board set 
title = #{title},
content = #{content}
where board_num = #{board_num} and user_id = #{user_id}
